function IdentityCodeValid( idnumber ) {

    idnumber = idnumber.replace( /^\s+/g, '' ).replace( /\s+$/g, '' );

    if( idnumber.length == 0 ) {
        return {
            'error' : 100,
            'msg'   : "身份证号不能为空"
        };
    }

    // var city = {
    //     11 : "北京", 12 : "天津", 13 : "河北", 14 : "山西", 15 : "内蒙古", 21 : "辽宁", 22 : "吉林", 23 : "黑龙江",
    //     31 : "上海", 32 : "江苏", 33 : "浙江", 34 : "安徽", 35 : "福建", 36 : "江西", 37 : "山东",
    //     41 : "河南", 42 : "湖北", 43 : "湖南", 44 : "广东", 45 : "广西", 46 : "海南", 50 : "重庆", 51 : "四川", 52 : "贵州", 53 : "云南", 54 : "西藏",
    //     61 : "陕西", 62 : "甘肃", 63 : "青海", 64 : "宁夏", 65 : "新疆", 71 : "台湾", 81 : "香港", 82 : "澳门", 91 : "国外"
    // };
    var city = {
        11 : "32010", 12 : "32030", 13 : "32015", 14 : "32027", 15 : "32022", 21 : "31349", 22 : "32021", 23 : "32016",
        31 : "32026", 32 : "32019", 33 : "32034", 34 : "31538", 35 : "32012", 36 : "32020", 37 : "32025",
        41 : "32017", 42 : "32018", 43 : "29301", 44 : "59", 45 : "27885", 46 : "31412",
        50 : "32011", 51 : "32029", 52 : "32014", 53 : "32033", 54 : "32032",
        61 : "32028", 62 : "32013", 63 : "32024", 64 : "32023", 65 : "32031",
        71 : "台湾", 81 : "11994", 82 : "澳门", 91 : "国外"
    };



    var msg = "";

    if( !idnumber || !/^\d{6}(18|19|20)?\d{2}(0[1-9]|1[012])(0[1-9]|[12]\d|3[01])\d{3}(\d|X)$/i.test( idnumber ) ) {
        return {
            'error' : 101,
            'msg'   : "身份证号格式错误"
        };
    } else if( !city[ idnumber.substr( 0, 2 ) ] ) {
        return {
            'error' : 102,
            'msg'   : "身份证号地址编码错误"
        };
    } else {
        cityName = city[ idnumber.substr( 0, 2 ) ];
        birthday = '';
        sex      = '';
        //18位身份证需要验证最后一位校验位
        if( idnumber.length == 18 ) {
            code = idnumber.split( '' );
            //∑(ai×Wi)(mod 11)
            //加权因子
            var factor = [ 7, 9, 10, 5, 8, 4, 2, 1, 6, 3, 7, 9, 10, 5, 8, 4, 2 ];
            //校验位
            var parity = [ 1, 0, 'X', 9, 8, 7, 6, 5, 4, 3, 2 ];
            var sum    = 0;
            var ai     = 0;
            var wi     = 0;
            for( var i = 0; i < 17; i++ ) {
                ai = code[ i ];
                wi = factor[ i ];
                sum += ai * wi;
            }
            var last = parity[ sum % 11 ];
            if( parity[ sum % 11 ] != code[ 17 ] ) {
                return {
                    'error' : 110,
                    'msg'   : "身份证号校验位错误"
                };
            }
            birthday = idnumber.substring( 6, 10 ) + "-" + idnumber.substring( 10, 12 ) + "-" + idnumber.substring( 12, 14 );
            sex      = ( parseInt( idnumber.substring( 16, 17 ) ) % 2 == 0 ) ? 1 : 2;
        } else if( idnumber.length == 15 ) {
            birthday = "19" + idnumber.substring( 6, 8 ) + "-" + idnumber.substring( 8, 10 ) + "-" + idnumber.substring( 10, 12 );
            sex      = ( parseInt( idnumber.substring( 14, 15 ) ) % 2 == 0 ) ? 1 : 2;
        }
    }
    var ret = {
        'error' : 0,
        'msg'   : msg,
        'info'  : {
            'birthday' : birthday,
            'sex'      : sex,
            'city'     : cityName
        }
    };
    return ret;
}




